#!/bin/sh

LOGIN="docker login -u AWS -p  822423649500.dkr.ecr.eu-central-1.amazonaws.com/container"
tar -xf pack.tar.gz

cd pack
APP=`cat .env | grep AWSAPP | cut -d'=' -f2`
SERVER=`cat .env | grep AWSPROXY | cut -d'=' -f2`

eval $LOGIN

docker ps -a | awk '{print $1}' | xargs docker rm -f 
docker images | awk '{print $3}' | xargs docker image rm -f 

docker pull $APP
docker pull $SERVER

docker-compose up
